UPDATE tww_sys.dictionary_od_field  SET
   table_name = 'waste_water_treatment_plant',
   field_name = 'cod',
   field_name_en = 'cod',
   field_name_de = 'CSB',
   field_name_fr = 'DCO',
   field_name_it = 'zzz_CSB',
   field_name_ro = 'cod',
   field_description_en = 'Abbreviation for chemical oxygen demand (COD).',
   field_description_de = 'Abkürzung für den chemischen Sauerstoffbedarf. Die englische Abkürzung lautet COD. Mit einem starken Oxydationsmittel wird mehr oder weniger erfolgreich versucht, die organischen Verbindungen der Abwasserprobe zu CO2 und H2O zu oxydieren. Als Oxydationsmittel eignen sich Chromverbindungen verschiedener Wertigkeit (z.B. Kalium-Dichromat K2Cr2O7) und Manganverbindungen (z.B. KmnO4), wobei man unter dem CSB im Allgemeinen den chemischen Sauerstoffbedarf nach der Kalium-Dichromat-Methode) versteht. Das Resultat kann als Chromatverbrauch oder Kaliumpermanaganatverbrauch ausgedrückt werden (z.B. mg CrO4 2-/l oder mg KMnO4/l). Im allgemeinen ergibt die Kalium-Dichromat-Methode höhere Werte als mit Kaliumpermanganat. Das Verhältnis des CSB zum BSB5 gilt als Hinweis auf die Abbaubarkeit der organischen Abwasserinhaltsstoffe. Leicht abbaubare häusliche Abwässer haben einen DSB/BSB5-Verhältnis von 1 bis 1,5. Schweres abbaubares, industrielles Abwasser ein Verhältnis von über 2. (arb)',
   field_description_fr = 'Elle représente la teneur totale de l’eau en matières organiques, qu’elles soient ou non biodégradables. Le principe repose sur la recherche d’un besoin d’oxygène de l’échantillon pour dégrader la matière organique. Mais dans ce cas, l’oxygène est fourni par un oxydant puissant (le bichromate de potassium). La réaction (Afnor T90-101) est pratiquée à chaud (150°C) en présence d’acide sulfurique, et après 2 h on mesure la quantité d’oxydant restant. Là encore, le résultat s’exprime en milligrammes d’O2 par litre d’effluent.  Le rapport entre DCO/DBO5 est d’environ 2 à 2.7 pour une eau usée domestique ; au-delà, il y a vraisemblablement présence d’eaux industrielles résiduaires.',
   field_description_it = 'zzz_Abkürzung für den chemischen Sauerstoffbedarf. Die englische Abkürzung lautet COD. Mit einem starken Oxydationsmittel wird mehr oder weniger erfolgreich versucht, die organischen Verbindungen der Abwasserprobe zu CO2 und H2O zu oxydieren. Als Oxydationsmittel eignen sich Chromverbindungen verschiedener Wertigkeit (z.B. Kalium-Dichromat K2Cr2O7) und Manganverbindungen (z.B. KmnO4), wobei man unter dem CSB im Allgemeinen den chemischen Sauerstoffbedarf nach der Kalium-Dichromat-Methode) versteht. Das Resultat kann als Chromatverbrauch oder Kaliumpermanaganatverbrauch ausgedrückt werden (z.B. mg CrO4 2-/l oder mg KMnO4/l). Im allgemeinen ergibt die Kalium-Dichromat-Methode höhere Werte als mit Kaliumpermanganat. Das Verhältnis des CSB zum BSB5 gilt als Hinweis auf die Abbaubarkeit der organischen Abwasserinhaltsstoffe. Leicht abbaubare häusliche Abwässer haben einen DSB/BSB5-Verhältnis von 1 bis 1,5. Schweres abbaubares, industrielles Abwasser ein Verhältnis von über 2. (arb)',
   field_description_ro = '',
   field_mandatory = ARRAY['GEP_Verband','GEP_Traegerschaft','PAA']::tww_od.plantype[],
   field_visible = 'true',
   field_datatype = 'smallint',
   field_unit_en = '[gCOD/m3]',
   field_unit_description_en = 'Chemical oxygen demand [gCOD/m3]',
   field_unit_de = '[gCSB/m3]',
   field_unit_description_de = 'Chemischer Sauerstoffbedarf [gCSB/m3]',
   field_unit_fr = '[gDCO/m3]',
   field_unit_description_fr = 'Demande chimique en oxygène [gDCO/m3]',
   field_unit_it = '[gDCO/m3]',
   field_unit_description_it = 'Domanda chimica di ossigeno [gDCO/m3].',
   field_unit_ro = 'rrr_[gCSB/m3]',
   field_unit_description_ro = 'rrr_Chemischer Sauerstoffbedarf [gCSB/m3]',
   field_max = 1000,
   field_min = 0
WHERE (class_id = 9 AND attribute_id = 3514);

