CREATE FUNCTION tww_od.on_wastewater_structure_update() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
  _ws_obj_id TEXT;
BEGIN
  -- Prevent recursion
  IF COALESCE(OLD.identifier, '') = COALESCE(NEW.identifier, '') THEN
    RETURN NEW;
  END IF;
  _ws_obj_id = OLD.obj_id;
  SELECT tww_od.update_wastewater_structure_label(_ws_obj_id) INTO NEW._label;

  IF OLD.fk_main_cover != NEW.fk_main_cover THEN
    EXECUTE tww_od.update_depth(_ws_obj_id);
  END IF;


  RETURN NEW;
END; $$;


